class Breadcrumbs{constructor(e){this.$element=$(e),this.setListeners()}setListeners(){const s=document.location.pathname.split("/").filter(e=>""!==e);1<=s.length&&this.$element.append('/ <a href="/">Главная</a> /'),s.forEach((e,t)=>{let a=s.slice(0,t+1).join("/");Translater.TransalteString(s[t],"ru").then(e=>{this.$element.append(`<a href="/${a}">${Capitalize.CapitalizeSting(e.split("%").join(""))}</a> /`)})})}}$(()=>{$(".js-breadcrumb").each((e,t)=>new Breadcrumbs(t))});
$(document).ready(function(){$(".js-burger").on("click tap",function(){$(".js-burger").toggleClass("active-burger");var e=$(".header").outerHeight(!0);$(".js-mobile-menu").css({top:e+"px"}),$(".js-mobile-menu").toggleClass("menu-show")})});
class Capitalize{static CapitalizeSting(a){return a.charAt(0).toUpperCase()+a.slice(1)}}
class Translater{static async TransalteString(a,t){return translate(a,t)}}